import React, { useState } from 'react'
import Navbar from './NavBar'
import './Table.css'
import {useLocation} from 'react-router-dom'
import PrimaryLink from '../axios/primary_link'
const UserProfile = ()=>{
    const location = useLocation()
    const id = location.state.params;
    const url = '/User/getByUserId/'+ id;

    const renderHeader = () => {
        let headerElement = ['Name', 'Email', 'Address', 'City', 'Province', 'ZIP']

        return headerElement.map((key, index) => {
            return <th key={index}>{key.toUpperCase()}</th>
        })
    }

    
    const [reloadbool,setReloadBool] = React.useState(true)
    const [name,setname] = useState('');
    const [email,setemail] = useState('');
    const [address,setaddress] = useState('');
    const [city,setcity] = useState('');
    const [province,setprovince] = useState('');
    const [zip,setzip] = useState('');

    React.useEffect(async()=>{
        //api call
        const response = await PrimaryLink.get(url, 
            {headers:{'x-access-token':localStorage.getItem("token")}})
              .then((response) => {
                console.log(response.data);
                setname('chandru');
                setemail('test@gmail.com');
                setaddress('aadd');
                setprovince('canada');
                setzip('111');
                setcity('temp');
              });
    },[])

    const renderBody = () => {
        return( <tr>
            <td>{name}</td>
            <td>{email}</td>
            <td>{address}</td>
            <td>{city}</td>
            <td>{province}</td>
            <td>{zip}</td>
        </tr>)
               
    }

    return(
        <div>
           <Navbar /> 
           <br/><br/>
           <div className="mytable"> 
           <table id="book">
                <thead>
                    <tr>{renderHeader()}</tr>
                </thead>
                <tbody>
                    {renderBody()}
                </tbody>
            </table>
            </div>
        </div>
    )
}

export default UserProfile;
